import React from 'react'
import { ThemeConsumer } from '../contexts/theme.js'

export default function Nav() {
	return(
		<ThemeConsumer>
			{({ theme, toggleTheme }) => {
				<nav className='row space-between'>
					<button style={{ fontSize: 30}}
					className='btn-clear'
					onClick={toggleTheme}
					>
					{ theme === 'light' ? 'Light Mode' : 'Dark Mode' }	
					</button>
				</nav>
			}}
		</ThemeConsumer>
		)
}
