			{({ theme, toggleTheme }) => (
				<nav className='row space-between'>
					<button 
					style={{fontSize: 30}}
					className='btn-clear'
					onClick={toggleTheme}
					>
					{ theme === 'light' ? '‚òÄÔ∏è' : 'üåô' }	
					</button>
				</nav>
			)}
		</ThemeConsumer>
	)
}
